    private val maleNameCount = names["male"]?.size ?: 0
    private val femaleNameCount = names["female"]?.size ?: 0
    private val maleSurnameCount = surnames["male"]?.size ?: 0
    private val femaleSurnameCount = surnames["female"]?.size ?: 0
    private val aboutsCount = abouts.size
    private val placesCount = places.size

    private fun randomPerson(id: Int): Person {
        val sex = if (Math.random() * 500 < 250) "male" else "female"
        val nameCount = if (sex == "male") maleNameCount else femaleNameCount
        val surnameCount = if (sex == "male") maleSurnameCount else femaleSurnameCount

        val placeEndIndex = (Math.random() * randomPlaces.size).toInt()
        val placeStartIndex = (Math.random() * placeEndIndex).toInt()

        return Person(
                id = id.toString(),
                name = names[sex]!![(Math.random() * nameCount).toInt()],
                surname = surnames[sex]!![(Math.random() * surnameCount).toInt()],
                about = abouts[(Math.random() * aboutsCount).toInt()],
                place = places[(Math.random() * placesCount).toInt()],
                places = randomPlaces.subList(placeStartIndex, placeEndIndex),
                avatar = "https://randomuser.me/api/portraits/${
                if (sex == "male") "men" else "women"
                }/${(Math.random() * 100).toInt()}.jpg"
        )
    }

    private fun randomPlace(id: Int): Place {
        return Place(
                name = places[(Math.random() * placesCount).toInt()]
        )
    }

